$('.nav a').click(function () {
    $('.navbar-collapse').collapse('hide');
});

var apps = [$("#index"),$("#CalipsoBazar"),$("#Calipso7sfx"),$("#CalipsoPing"),
			$("#CalipsoTools"),$("#CalipsoSense"),$("#SyncNotify"),$("#CalipsoCef"),
			$("#CalipsoReSync"),$('#catalogue'),$('#dependencies')];

function btnMain(app){

	for(var i=0;i<apps.length;i++){

		if(apps[i].attr('id')==app) apps[i].show();
		else apps[i].hide();
	}

}